import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

const moviesApi = createApi({
  reducerPath: 'movies',
  baseQuery: fetchBaseQuery({
    baseUrl: 'https://api.themoviedb.org/3/',
  }),
  endpoints(builder) {
    return {
      fetchMovies: builder.query({
        query: (query) => {
          return {
            url: '/discover/movie',
            method: 'GET',
            params: {
              language: lg,
              page: page,
            },
            headers: {
              accept: 'application/json',
              Authorization:
                'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIzNzBjMzk2NzdmNTYwYTZiMmE5ZGRhZmFmMjU1YzE4NiIsInN1YiI6IjY1MjNlMDBkMDcyMTY2NDViNGZmNWFkOSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.SNyfItT9pLMgujvWBB3NJXzrWpsSKkA8qO5jqleeuiY',
            },
          };
        },
      }),

      searchMovies: builder.query({
        query: (searchTerm, page) => {
          return {
            url: '/search/movie',
            method: 'GET',
            params: {
              query: searchTerm,
              page: page,
              // include_adult: 'false',
              // language: 'en-US',
            },
            headers: {
              accept: 'application/json',
              Authorization:
                'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIzNzBjMzk2NzdmNTYwYTZiMmE5ZGRhZmFmMjU1YzE4NiIsInN1YiI6IjY1MjNlMDBkMDcyMTY2NDViNGZmNWFkOSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.SNyfItT9pLMgujvWBB3NJXzrWpsSKkA8qO5jqleeuiY',
            },
          };
        },
      }),
    };
  },
});

export const { useFetchMoviesQuery, useSearchMoviesQuery } = moviesApi;
export { moviesApi };
