import { useState, useEffect, useRef } from 'react';
import { useSearchMoviesQuery } from '../store';
import SearchField from '../components/utils/SearchField';
import GridSkeleton from '../components/utils/GridSkeleton';
import MoviesList from '../components/Movies/MoviesList';
import NoContent from '../components/Movies/NoContent';
import Button from '../components/utils/Button';

function Home() {
  const [searchMovies, setSearchMovies] = useState('');
  const [searchPagination, setSearchPagination] = useState(0);
  const [moviesList, setMoviesList] = useState([]);
  const prevSearchDataRef = useRef();
  const prevPagination = useRef();

  const previousQueryRef = useRef();

  const [searchObject, setSearchObject] = useState({ query: '', page: 0 });
  console.log('old', searchObject);

  const {
    data: searchData,
    error: errorSearch,
    isFetching: isFetchingSearch,
    isSuccess,
  } = useSearchMoviesQuery(
    {
      searchTerm: searchObject.query,
      page: searchObject.page,
    },
    {
      skip: searchObject.query === '',
      // refetchOnMountOrArgChange: true,
    }
  );

  const onSearch = (query) => {
    if (moviesList.length) setMoviesList([]);
    if (query === '') {
      setSearchObject({
        query: '',
        page: 0,
      });
    } else {
      setSearchObject({
        query: query,
        page: 1,
      });
    }
  };

  // useEffect(() => {
  //   if (searchData && searchData.results) {
  //     setMoviesList((prevMovies) => [...prevMovies, ...searchData.results]);
  //   }
  // }, [searchObject.query, searchData]);

  useEffect(() => {
    previousQueryRef.current = searchObject.query;
    console.log('lol');
  }, [searchObject.query]);

  useEffect(() => {
    if (searchObject.query !== previousQueryRef.current && searchData.results) {
      console.log('schimbat');
      // Your logic to handle the query change goes here
      setMoviesList(searchData.results);
    }
  }, [searchObject.query, searchData]);

  const loadNextPage = () => {
    setSearchObject((prevSearchObject) => ({
      ...prevSearchObject,
      page: prevSearchObject.page + 1,
    }));
  };

  // useEffect(() => {
  //   if (searchData && searchData.results && isSuccess === true) {
  //     console.log('=== intra ba', searchData, isSuccess);
  //     if (searchPagination === 1) setMoviesList(searchData.results);
  //     else {
  //       setMoviesList((prevMovies) => [...prevMovies, ...searchData.results]);
  //     }
  //     console.log('data');
  //   }
  // }, [searchData, searchPagination, isSuccess]);

  let content;

  if (errorSearch) {
    content = 'Error';
  } else if (isFetchingSearch && !moviesList.length) {
    content = <GridSkeleton count={6} />;
  } else if (
    searchObject.query !== '' &&
    !moviesList.length &&
    !isFetchingSearch
  ) {
    content = (
      <NoContent>
        You have searched for something that does not exist.
      </NoContent>
    );
  } else if (searchObject.query !== '') {
    content = (
      <>
        <MoviesList movies={moviesList} />
        <div className="loading-header">
          {searchObject.page < searchData?.total_pages ? (
            <Button onClick={loadNextPage}>Load more movies</Button>
          ) : (
            <p>That's all the results for your search</p>
          )}
        </div>
        {isFetchingSearch && <GridSkeleton count={6} />}
      </>
    );
  } else content = 'wtf';

  return (
    <div className="safePage">
      <SearchField onSearch={onSearch} />
      {content}
    </div>
  );
}
export default Home;
