import { AiOutlineHeart } from 'react-icons/ai';

function MovieItem({ movie }) {
  const handleImageError = (event) => {
    event.target.style.display = 'none'; // Hide the broken image
    event.target.nextElementSibling.style.display = 'block'; // Display text element
  };

  return (
    <div className="movies-item">
      <img
        src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`}
        alt={movie.title}
        onError={(event) => {
          event.target.style.display = 'none'; // Hide the broken image
        }}
      />
      <AiOutlineHeart className="movies-item-heart" />
      <p className="movies-item-title">{movie.title}</p>
      <img
        src="/path-to-your-fallback-image.jpg" // Provide the path to your fallback image
        alt="Image not available from API" // Alt text for the fallback image
        style={{ display: 'none' }}
      />
    </div>
  );
}

export default MovieItem;
