import MoviesList from './components/Movies/MoviesList';
import Navbar from './components/Navigation/Navbar';
import { useState, useEffect } from 'react';
import { useSearchMoviesQuery } from './store';
import SearchField from './components/utils/SearchField';
import GridSkeleton from './components/utils/GridSkeleton';

function App() {
  const [searchMovies, setSearchMovies] = useState('');
  const [oldSearch, setOldSearch] = useState('');
  const [searchPagination, setSearchPagination] = useState(1);
  const [newSearch, setNewSearch] = useState([]);

  const {
    data: searchData,
    error: errorSearch,
    isFetching: isFetchingSearch,
  } = useSearchMoviesQuery(
    {
      searchTerm: searchMovies,
      page: searchPagination,
    },
    {
      skip: searchMovies === '',
    }
  );

  const onSearch = (query) => {
    if (query !== '' && query !== oldSearch) {
      setOldSearch(query);
      setNewSearch([]);
      setSearchPagination(1);
      setSearchMovies(query);
    } else if (query === '' && query !== oldSearch) {
      setNewSearch([]);
      setOldSearch('');
    }
  };

  useEffect(() => {
    if (searchData && searchData.results) {
      setNewSearch((prevMovies) => [...prevMovies, ...searchData.results]);
    }
  }, [searchData]);

  const handleLoadMoreSearches = () => {
    setSearchPagination(searchPagination + 1);
  };

  let content;

  if (errorSearch) {
    content = 'Error';
  } else if (isFetchingSearch && !newSearch.length) {
    content = <GridSkeleton count={6} />;
  } else if (newSearch.length) {
    content = (
      <>
        <MoviesList
          movies={newSearch}
          loadMoreMovies={handleLoadMoreSearches}
        />
        {isFetchingSearch && <GridSkeleton count={6} />}
      </>
    );
  } else {
    content = 'Nothing';
  }

  return (
    <div>
      <Navbar />
      <SearchField onSearch={onSearch} />
      {content}
    </div>
  );
}
export default App;
