import MoviesList from './components/Movies/MoviesList';
import Navbar from './components/Navigation/Navbar';
import { useState, useEffect } from 'react';
import { useFetchMoviesQuery, useSearchMoviesQuery } from './store';
import SearchField from './components/utils/SearchField';
import MovieItem from './components/Movies/MovieItem';
import GridSkeleton from './components/utils/GridSkeleton';

function App() {
  const [moviePagination, setMoviePagination] = useState(1);
  const { data, error, isFetching } = useFetchMoviesQuery(moviePagination);
  const [movies, setMovies] = useState([]);

  const [searchMovies, setSearchMovies] = useState('slam');
  const [searchPagination, setSearchPagination] = useState(1);
  const {
    data: searchData,
    error: errorSearch,
    isFetching: isFetchingSearch,
  } = useSearchMoviesQuery(searchMovies, searchPagination);

  const onSearch = (query) => {
    if (query !== '') setSearchMovies(query);
  };

  useEffect(() => {
    if (data && data.results) {
      setMovies((prevMovies) => [...prevMovies, ...data.results]);
    }
  }, [data]);

  const handleLoadMoreMovies = () => {
    setMoviePagination(moviePagination + 1);
  };

  return (
    <div>
      <Navbar />
      <SearchField onSearch={onSearch} />
      {/* <MoviesList
        movies={movies}
        error={error}
        isFetching={isFetching}
        loadMoreMovies={handleLoadMoreMovies}
      /> */}

      {searchData?.results ? (
        <MoviesList
          movies={searchData.results}
          error={errorSearch}
          isFetching={isFetchingSearch}
          loadMoreMovies={handleLoadMoreMovies}
        />
      ) : (
        <GridSkeleton count={6} />
      )}
    </div>
  );
}
export default App;
