import { useFetchMoviesQuery } from '../../store';
import Button from '../utils/Button';
import MovieItem from './MovieItem';
import { useState, useEffect } from 'react';
function MoviesList() {
  const [moviePagination, setMoviePagination] = useState(1);
  const { data, error, isFetching } = useFetchMoviesQuery(moviePagination);
  const [movies, setMovies] = useState([]);
  const [scrollPosition, setScrollPosition] = useState(0);

  const handleLoadMoreMovies = () => {
    setScrollPosition(window.scrollY);
    setMoviePagination(moviePagination + 1);
  };

  useEffect(() => {
    if (data && data.results) {
      setMovies((prevMovies) => [...prevMovies, ...data.results]);
      // Use functional update to set scrollPosition to avoid infinite updates
      setScrollPosition((prevScrollPosition) => {
        window.scrollTo(0, prevScrollPosition);
        return prevScrollPosition; // Return the same value
      });
    }
  }, [data, scrollPosition]);

  let content;

  if (isFetching) content = <div>Loading..</div>;
  else if (error) content = <div>Error</div>;
  else {
    const movieItems = movies.map((movie) => {
      return <MovieItem key={movie.id} movie={movie} />;
    });

    content = (
      <>
        <div className="movies-grid">{movieItems}</div>
        <Button onClick={handleLoadMoreMovies}>Load more movies</Button>
      </>
    );
  }
  return (
    <div>
      <h1>Movies list</h1>
      {content}
    </div>
  );
}

export default MoviesList;
