import { useState, useEffect, useRef } from 'react';
import { useSearchMoviesQuery } from '../store';
import SearchField from '../components/utils/SearchField';
import GridSkeleton from '../components/utils/GridSkeleton';
import MoviesList from '../components/Movies/MoviesList';
import NoContent from '../components/Movies/NoContent';
import Button from '../components/utils/Button';

function Home() {
  const [searchMovies, setSearchMovies] = useState('');
  const [searchPagination, setSearchPagination] = useState(0);
  const [moviesList, setMoviesList] = useState([]);
  const prevSearchDataRef = useRef();

  const {
    data: searchData,
    error: errorSearch,
    isFetching: isFetchingSearch,
    isSuccess,
  } = useSearchMoviesQuery(
    {
      searchTerm: searchMovies,
      page: searchPagination,
    },
    {
      skip: searchMovies === '',
      // refetchOnMountOrArgChange: true,
    }
  );

  const onSearch = (query) => {
    setSearchMovies(query);
    setSearchPagination(1);

    if (query === '') {
      // setMoviesList([]);
    }
  };

  const loadNextPage = () => {
    setSearchPagination((prev) => prev + 1);
  };

  useEffect(() => {
    const prevSearchData = prevSearchDataRef.current;
    if (searchData !== prevSearchData && searchData.results) {
      if (searchPagination === 1) setMoviesList(searchData.results);
      else {
        setMoviesList((prevMovies) => [...prevMovies, ...searchData.results]);
      }
    }
    prevSearchDataRef.current = searchData;
  }, [searchData, searchPagination]);

  let content;

  if (errorSearch) {
    content = 'Error';
  } else if (isFetchingSearch && !moviesList.length) {
    content = <GridSkeleton count={6} />;
  } else if (searchMovies !== '' && !moviesList.length && !isFetchingSearch) {
    content = (
      <NoContent>
        You have searched for something that does not exist.
      </NoContent>
    );
  } else if (searchMovies !== '') {
    content = (
      <>
        <MoviesList movies={moviesList} />
        <div className="loading-header">
          {searchPagination < searchData?.total_pages ? (
            <Button onClick={loadNextPage}>Load more movies</Button>
          ) : (
            <p>That's all the results for your search</p>
          )}
        </div>
        {isFetchingSearch && <GridSkeleton count={6} />}
      </>
    );
  }

  return (
    <div className="safePage">
      <SearchField onSearch={onSearch} />
      {content}
    </div>
  );
}
export default Home;
