import { useFetchMoviesQuery, useSearchMoviesQuery } from '../../store';
import Button from '../utils/Button';
import MovieItem from './MovieItem';
import { useState, useEffect } from 'react';
import GridSkeleton from '../utils/GridSkeleton';
import SearchField from '../utils/SearchField';
function MoviesList() {
  const [moviePagination, setMoviePagination] = useState(1);
  const { data, error, isFetching } = useFetchMoviesQuery(moviePagination);
  const [movies, setMovies] = useState([]);

  const [searchMovies, setSearchMovies] = useState('slam');
  const [searchPagination, setSearchPagination] = useState(1);
  const { searchData, searchError, searchIsFetching } = useSearchMoviesQuery(
    searchMovies,
    searchPagination,
    {
      skip: true,
    }
  );

  const handleLoadMoreMovies = () => {
    setMoviePagination(moviePagination + 1);
  };

  const handleSearch = (query) => {
    setSearchMovies(query);
  };

  useEffect(() => {
    if (data && data.results) {
      setMovies((prevMovies) => [...prevMovies, ...data.results]);
    }
  }, [data]);

  let content;

  if (isFetching && !movies.length) content = <GridSkeleton count={6} />;
  else if (error) content = <div>Error</div>;
  // else if (searchData) content = 'sal';
  else
    content = (
      <>
        <SearchField onSearch={handleSearch} />
        <div className="movies-grid">
          {movies.map((movie) => (
            <MovieItem key={movie.id} movie={movie} />
          ))}
        </div>
        {movies.length && (
          <div className="loading-header">
            <Button onClick={handleLoadMoreMovies}>Load more movies</Button>
          </div>
        )}
        {isFetching ? <GridSkeleton count={6} /> : ''}
      </>
    );

  return (
    <div>
      <h1>Movies list</h1>
      {content}
    </div>
  );
}

export default MoviesList;
