import MoviesList from './components/Movies/MoviesList';
import Navbar from './components/Navigation/Navbar';
import { useState, useEffect, useRef } from 'react';
import { useSearchMoviesQuery, useFetchFavoriteMoviesQuery } from './store';
import SearchField from './components/utils/SearchField';
import GridSkeleton from './components/utils/GridSkeleton';
import { useDispatch } from 'react-redux';
import { setFavoriteMovies } from './store/slices/favoriteMoviesSlice';
import Favorites from './components/Movies/Favorites';

function App() {
  const dispatch = useDispatch();
  const [searchMovies, setSearchMovies] = useState('');
  const [searchPagination, setSearchPagination] = useState(0);
  const [moviesList, setMoviesList] = useState([]);
  const prevSearchDataRef = useRef();

  // const [favoriteMoviesList, setFavoriteMoviesList] = useState([]);

  const {
    data: favoriteMovies,
    error: errorFetchingFavoriteMovies,
    isFetching: isFetchingFavoriteMovies,
  } = useFetchFavoriteMoviesQuery(1);

  useEffect(() => {
    if (favoriteMovies?.results) {
      dispatch(setFavoriteMovies(favoriteMovies.results));
    }
  }, [favoriteMovies, dispatch]);

  const {
    data: searchData,
    error: errorSearch,
    isFetching: isFetchingSearch,
  } = useSearchMoviesQuery(
    {
      searchTerm: searchMovies,
      page: searchPagination,
    },
    {
      skip: searchMovies === '',
    }
  );

  const onSearch = (query) => {
    setSearchMovies(query);
    setSearchPagination(1);
  };

  const loadNextPage = () => {
    setSearchPagination((prev) => prev + 1);
  };

  useEffect(() => {
    const prevSearchData = prevSearchDataRef.current;
    if (searchData !== prevSearchData && searchData.results) {
      if (searchPagination === 1) setMoviesList(searchData.results);
      else {
        setMoviesList((prevMovies) => [...prevMovies, ...searchData.results]);
      }
    }
    prevSearchDataRef.current = searchData;
  }, [searchData, searchPagination]);

  // useEffect(() => {
  //   if (favoriteMovies && favoriteMovies.results) {
  //     setFavoriteMoviesList((prev) => [...prev, ...favoriteMovies.results]);
  //   }
  // }, [favoriteMovies]);

  let content;

  if (errorSearch) {
    content = 'Error';
  } else if (isFetchingSearch && !moviesList.length) {
    content = <GridSkeleton count={6} />;
  } else if (searchMovies !== '') {
    content = (
      <>
        <MoviesList movies={moviesList} nextPage={loadNextPage} />
        {isFetchingSearch && <GridSkeleton count={6} />}
      </>
    );
  }

  // let favoriteContent;

  // if (errorFetchingFavoriteMovies) {
  //   content = 'Error';
  // } else if (isFetchingFavoriteMovies && !favoriteMoviesList.length) {
  //   content = <GridSkeleton count={6} />;
  // } else
  //   content = (
  //     <>
  //       <MoviesList movies={favoriteMoviesList} nextPage={loadNextPage} />
  //       {isFetchingFavoriteMovies && <GridSkeleton count={6} />}
  //     </>
  //   );

  return (
    <div>
      <Navbar />
      <SearchField onSearch={onSearch} />
      {content}
      <Favorites />
    </div>
  );
}
export default App;
