import { useFetchMoviesQuery } from '../../store';
import Button from '../utils/Button';
import MovieItem from './MovieItem';
function MoviesList() {
  const { data, error, isFetching } = useFetchMoviesQuery(2);
  console.log(data);

  let content;
  if (isFetching) content = <div>Loading..</div>;
  else if (error) content = <div>Error</div>;
  else {
    const movieItems = data.results.map((movie) => {
      return <MovieItem key={movie.id} movie={movie} />;
    });

    content = (
      <>
        {movieItems}
        <Button>Load more movies..</Button>
      </>
    );
  }
  return (
    <div>
      <h1>Movies list</h1>
      <div className="movies-grid"> {content}</div>
    </div>
  );
}

export default MoviesList;
