import Button from '../utils/Button';
import MovieItem from './MovieItem';
import GridSkeleton from '../utils/GridSkeleton';
function MoviesList({ movies, error, isFetching, loadMoreMovies }) {
  // const [searchMovies, setSearchMovies] = useState('slam');
  // const [searchPagination, setSearchPagination] = useState(1);
  // const { searchData, searchError, searchIsFetching } = useSearchMoviesQuery(
  //   searchMovies,
  //   searchPagination
  // );

  const handleLoadMoreMovies = () => {
    loadMoreMovies();
  };

  // const handleSearch = (query) => {
  //   if (query !== '') setSearchMovies(query);
  // };

  let content;

  if (isFetching && !movies.length) content = <GridSkeleton count={6} />;
  else if (error) content = <div>Error</div>;
  else
    content = (
      <>
        <div className="movies-grid">
          {movies.map((movie) => (
            <MovieItem key={movie.id} movie={movie} />
          ))}
        </div>
        {movies.length && (
          <div className="loading-header">
            <Button onClick={handleLoadMoreMovies}>Load more movies</Button>
          </div>
        )}
        {isFetching ? <GridSkeleton count={6} /> : ''}
      </>
    );

  return (
    <div>
      <h1>Movies list</h1>
      {content}
    </div>
  );
}

export default MoviesList;
