import { AiOutlineHeart, AiFillHeart } from 'react-icons/ai';
import { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { setFavoriteMovies } from '../../store';
import { useNavigate } from 'react-router-dom';
import FavoriteHeart from '../utils/FavoriteHeart';

function MovieItem({ movie }) {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [isImageBroken, setIsImageBroken] = useState(false);
  // const [isFavorite, setIsFavorite] = useState(false);

  const handleClick = () => {
    navigate(`/movies/${movie.id}`);
  };

  const handleImageError = () => {
    setIsImageBroken(true);
  };

  useEffect(() => {
    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];
    const isFavorite = favorites.some((favorite) => favorite.id === movie.id);
    setIsFavorite(isFavorite);
  }, [movie.id]);

  // const toggleFavorite = () => {
  //   const favorites = JSON.parse(localStorage.getItem('favorites')) || [];

  //   if (isFavorite) {
  //     const updatedFavorites = favorites.filter(
  //       (favMovie) => favMovie.id !== movie.id
  //     );
  //     localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
  //     dispatch(setFavoriteMovies(updatedFavorites));
  //   } else {
  //     const updatedFavorites = [...favorites, movie];
  //     localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
  //     dispatch(setFavoriteMovies(updatedFavorites));
  //   }

  //   setIsFavorite(!isFavorite);
  // };

  const imageSrc = isImageBroken
    ? '/no-image-fallback.png'
    : `https://image.tmdb.org/t/p/original/${movie.poster_path}`;

  // const heartIcon = isFavorite ? (
  //   <AiFillHeart
  //     className="movies-item-heart"
  //     onClick={toggleFavorite}
  //     style={{ color: 'red' }}
  //   />
  // ) : (
  //   <AiOutlineHeart className="movies-item-heart" onClick={toggleFavorite} />
  // );

  return (
    <div className="movies-item">
      <img
        src={imageSrc}
        alt={movie.title}
        onError={handleImageError}
        onClick={handleClick}
      />

      {/* {heartIcon} */}
      <FavoriteHeart movie={movie} />

      <p className="movies-item-title">{movie.title}</p>
    </div>
  );
}

export default MovieItem;
