import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

const moviesApi = createApi({
  reducerPath: 'movies',
  baseQuery: fetchBaseQuery({
    baseUrl: 'https://api.themoviedb.org/3/',
  }),
  endpoints(builder) {
    return {
      fetchMovies: builder.query({
        query: (page) => {
          return {
            url: '/discover/movie',
            method: 'GET',
            params: {
              language: 'en-US',
              page: page,
            },
            headers: {
              accept: 'application/json',
              Authorization:
                'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIzNzBjMzk2NzdmNTYwYTZiMmE5ZGRhZmFmMjU1YzE4NiIsInN1YiI6IjY1MjNlMDBkMDcyMTY2NDViNGZmNWFkOSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.SNyfItT9pLMgujvWBB3NJXzrWpsSKkA8qO5jqleeuiY',
            },
          };
        },
        onSuccess: (data) => {
          return new Promise((resolve) => {
            setTimeout(() => {
              resolve(data);
            }, 2000); // 2 seconds delay
          });
        },
      }),
    };
  },
});

export const { useFetchMoviesQuery } = moviesApi;
export { moviesApi };
