import MoviesList from './components/Movies/MoviesList';
import Navbar from './components/Navigation/Navbar';
import { useState, useEffect } from 'react';
import { useSearchMoviesQuery } from './store';
import SearchField from './components/utils/SearchField';
import GridSkeleton from './components/utils/GridSkeleton';

function App() {
  const [searchMovies, setSearchMovies] = useState('');
  const [oldSearchedQuery, setOldSearchedQuery] = useState('');
  const [searchPagination, setSearchPagination] = useState(0);
  const [searchedList, setSearchedList] = useState([]);

  const {
    data: searchData,
    error: errorSearch,
    isFetching: isFetchingSearch,
  } = useSearchMoviesQuery({
    searchTerm: searchMovies,
    page: searchPagination,
  });

  const onSearch = (query) => {
    if (query !== '' && query !== oldSearchedQuery) {
      setOldSearchedQuery(query);
      setSearchedList([]);
      setSearchPagination(1);
      setSearchMovies(query);
    } else if (query === '' && query !== oldSearchedQuery) {
      setSearchedList([]);
      setOldSearchedQuery('');
    }
  };

  useEffect(() => {
    if (searchData && searchData.results) {
      setSearchedList((prevMovies) => [...prevMovies, ...searchData.results]);
    }
  }, [searchData?.results]);

  const handleLoadMoreSearches = () => {
    setSearchPagination(searchPagination + 1);
  };

  let content;

  if (errorSearch) {
    content = 'Error';
  } else if (isFetchingSearch && !searchedList.length) {
    content = <GridSkeleton count={6} />;
  } else if (
    !isFetchingSearch &&
    !searchedList.length &&
    oldSearchedQuery !== ''
  ) {
    content = "There's nothing to display";
  } else if (searchedList.length) {
    content = (
      <>
        <MoviesList
          movies={searchedList}
          loadMoreMovies={handleLoadMoreSearches}
        />
        {isFetchingSearch && <GridSkeleton count={6} />}
      </>
    );
  } else {
    content = 'Nothing';
  }

  return (
    <div>
      <Navbar />
      <SearchField onSearch={onSearch} />
      {content}
    </div>
  );
}
export default App;
