import React, { useState } from 'react';
import { AiOutlineHeart } from 'react-icons/ai';

function MovieItem({ movie }) {
  const [isImageBroken, setIsImageBroken] = useState(false);

  const handleImageError = () => {
    setIsImageBroken(true);
  };

  return (
    <div className="movies-item">
      <img
        src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`}
        alt={movie.title}
        onError={handleImageError}
        style={{ display: isImageBroken ? 'none' : 'block' }}
      />
      {!isImageBroken && <AiOutlineHeart className="movies-item-heart" />}
      <p className="movies-item-title">{movie.title}</p>
      {isImageBroken && (
        <p className="movies-item-error">
          Image not available from API. Please fix.
        </p>
      )}
    </div>
  );
}

export default MovieItem;
